import { Component } from '@angular/core';

@Component({
  selector: 'app-admin-dashboard',
  templateUrl: './admin-dashboard.component.html',
  styleUrls: ['./admin-dashboard.component.css']
})
export class AdminDashboardComponent {

}
<h1>Admin Dashboard</h1>
<main>
  <ol class="gradient-list">
  <a routerLink="./getAllBills"><li>Get All Bill Reports</li></a>
  <a routerLink="./getBillsByYear"><li>Get All Bill Reports By Year</li></a>
  <a routerLink="./getBillsByMonth"><li>Get All Bill Reports My Month</li></a>
  <a routerLink="./getBillsByArea"><li>Get All Bill Reports By Area</li></a>
  <a routerLink="./getBillsByCity"><li>Get All Bill Reports By City</li></a>
  <a href=""><li>Add units for a consumer</li></a>
</ol>

</main>
