import { Component } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { Bill } from '../bill';
import { BillServiceService } from '../bill-service.service';

@Component({
  selector: 'app-admin-get-bills-by-city-form',
  templateUrl: './admin-get-bills-by-city-form.component.html',
  styleUrls: ['./admin-get-bills-by-city-form.component.css'],
})
export class AdminGetBillsByCityFormComponent {
  AdminUserName!: string;
  AdminPassword!: string;
  city!: string;
  Bills: Bill[] = [];
  isHidden: boolean = false;
  checkBillsStatus() {
    return this.Bills.length != 0;
  }
  userForm = new FormGroup({
    username: new FormControl('', [Validators.required]),
    password: new FormControl('', [Validators.required]),
    city: new FormControl('', [Validators.required]),
  });

  constructor(private billService: BillServiceService) {}

  onSubmit(data: any) {
    this.AdminUserName = data.username;
    this.AdminPassword = data.password;
    this.city = data.city;
    this.billService
      .getBillsByCityAdmin(this.AdminUserName, this.AdminPassword, this.city)
      .subscribe((bills) => (this.Bills = bills));
    this.isHidden = true;
  }

  get getUserName() {
    return this.userForm.get('username');
  }
  get getPassword() {
    return this.userForm.get('password');
  }
  get getCity() {
    return this.userForm.get('city');
  }
  onBack() {
    this.isHidden = !this.isHidden;
    this.Bills = [];
  }
}
<div class="container1">
  <h1>Admin</h1>
  <h3>Get All Bill By City Reports</h3>
  <form
    [formGroup]="userForm"
    (ngSubmit)="onSubmit(userForm.value)"
    method="get"
    [class.hidden]="isHidden"
  >
    <div class="container2">
      <hr />
      <input
        type="text"
        id="username"
        name="username"
        formControlName="username"
        placeholder="Enter admin username"
      />
      <span
        style="color: red"
        *ngIf="getUserName && getUserName.invalid && getUserName.touched"
        >Username Required!!</span
      >
      <input
        type="password"
        id="password"
        name="password"
        formControlName="password"
        placeholder="Enter admin password"
      />
      <span
        style="color: red"
        *ngIf="getPassword && getPassword.invalid && getPassword.touched"
        >Password Required!!
      </span>
      <input
        type="text"
        id="city"
        name="city"
        formControlName="city"
        placeholder="Enter City"
      />
      <span
        style="color: red"
        *ngIf="getCity && getCity.invalid && getCity.touched"
        >City Required!!</span
      >
      <div>
        <button [disabled]="userForm.invalid" type="submit" class="submit">
          Submit
        </button>
      </div>
      <hr />
    </div>
  </form>
</div>

<div class="result" [class.hidden]="!isHidden">
  <div *ngIf="checkBillsStatus(); else noResultBlock">
    <div class="container">
      <h2>{{ city }} Bill</h2>
      <table class="table table-striped">
        <thead class="thead-dark">
          <tr class="table-dark">
            <th scope="col">Bill Id</th>
            <th scope="col">Consumer Id</th>
            <th scope="col">Month</th>
            <th scope="col">Year</th>
            <th scope="col">Units Consumed</th>
            <th scope="col">Total Amount</th>
          </tr>
        </thead>
        <tbody>
          <tr *ngFor="let bill of Bills" class="table-info">
            <td>{{ bill.billId }}</td>
            <td>{{ bill.consumerId.consumerId }}</td>
            <td>{{ bill.month }}</td>
            <td>{{ bill.year }}</td>
            <td>{{ bill.unitsConsumed }}</td>
            <td>{{ bill.totalAmount }}</td>
          </tr>
        </tbody>
      </table>
    </div>
  </div>
  <ng-template #noResultBlock>No Bills Found! </ng-template>
  <button (click)="onBack()" class="backbutton">Back</button>
</div>
