import { Component } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { of } from 'rxjs';
import { catchError } from 'rxjs/operators';
import { Bill } from '../bill';
import { BillServiceService } from '../bill-service.service';
import { Consumer } from '../consumer';

@Component({
  selector: 'app-admin-add-units-form',
  templateUrl: './admin-add-units-form.component.html',
  styleUrls: ['./admin-add-units-form.component.css'],
})
export class AdminAddUnitsFormComponent {
  AdminUserName!: string;
  AdminPassword!: string;
  consumerId!: number;
  year!: number;
  month!: string;
  unitsConsumed!: number;
  bill: Bill = new Bill(0, new Consumer(0, '', '', '', ''), 0, 0, '', 0);
  errorMsg!: string;

  isHidden: boolean = false;
  addUnitsForm = new FormGroup({
    username: new FormControl('', [Validators.required]),
    password: new FormControl('', [Validators.required]),
    unitsConsumed: new FormControl('', [Validators.required]),
    consumerId: new FormControl('', [Validators.required]),
    year: new FormControl('', [Validators.required]),
    month: new FormControl('', [Validators.required]),
  });

  checkResultStatus() {
    return this.bill.billId != 0 && this.bill.billId != undefined;
  }

  constructor(private billService: BillServiceService) {}

  onSubmit(data: any) {
    this.AdminUserName = data.username;
    this.AdminPassword = data.password;
    this.consumerId = data.consumerId;
    this.unitsConsumed = data.unitsConsumed;
    this.month = data.month;
    this.year = data.year;
    this.billService
      .addUnitsConsumed(
        this.AdminUserName,
        this.AdminPassword,
        this.month,
        this.year,
        this.consumerId,
        this.unitsConsumed
      )
      .pipe(
        catchError((error) => {
          this.errorMsg = `Error: ${error.error.message}`;
          return of([]);
        })
      )
      .subscribe((result) => {
        this.bill = result as unknown as Bill;
      });
    this.isHidden = true;
  }

  onBack() {
    this.isHidden = !this.isHidden;
    this.bill = new Bill(0, new Consumer(0, '', '', '', ''), 0, 0, '', 0);
  }
}
<div class="container1">
  <h1>Admin</h1>
  <h3>Add units consumed</h3>
  <form
    [formGroup]="addUnitsForm"
    (ngSubmit)="onSubmit(addUnitsForm.value)"
    method="get"
    [class.hidden]="isHidden"
  >
    <div class="container2">
      <hr />
      <label for="username">Username </label>
      <input
        type="text"
        id="username"
        name="username"
        formControlName="username"
        placeholder="Enter admin username"
        required
      />
      <br />
      <label for="password">Password </label>
      <input
        type="password"
        id="password"
        name="password"
        formControlName="password"
        placeholder="Enter admin password"
        required
      />
      <br />
      <label for="consumerId">Consumer ID </label>
      <input
        type="number"
        id="consumerId"
        name="consumerId"
        formControlName="consumerId"
        placeholder="Enter consumer ID"
        required
      />
      <br />
      <label for="unitsConsumed">Units Consumed </label>
      <input
        type="number"
        id="unitsConsumed"
        name="unitsConsumed"
        formControlName="unitsConsumed"
        placeholder="Enter units consumed"
        required
      />
      <br />
      <label for="year">Year </label>
      <input
        type="number"
        id="year"
        name="year"
        formControlName="year"
        placeholder="Enter year value"
        required
      />
      <br />
      <label for="month">Month </label>
      <select
        name="month"
        id="month"
        formControlName="month"
        required
        class="selectDiv"
      >
        <option value="">Select Month</option>
        <option value="January">January</option>
        <option value="February">February</option>
        <option value="March">March</option>
        <option value="April">April</option>
        <option value="May">May</option>
        <option value="June">June</option>
        <option value="July">July</option>
        <option value="August">August</option>
        <option value="September">September</option>
        <option value="October">October</option>
        <option value="November">November</option>
        <option value="December">December</option>
      </select>
      <br />
      <div>
        <button [disabled]="addUnitsForm.invalid" type="submit" class="submit">
          Submit
        </button>
      </div>
    </div>
  </form>
</div>
<div class="result" [class.hidden]="!isHidden">
  <div *ngIf="checkResultStatus(); else noResultBlock">
    <h3>Units Added Successfully</h3>
  </div>
  <ng-template #noResultBlock>
    <h2>{{ errorMsg }}</h2>
  </ng-template>
  <br />
  <div class="wrap">
    <button (click)="onBack()" class="back-button">Back</button>
  </div>
</div>
