import { Component } from '@angular/core';
import { FormGroup, FormControl, Validators } from '@angular/forms';
import { of } from 'rxjs';
import { catchError } from 'rxjs/operators';
import { Bill } from '../bill';
import { BillServiceService } from '../bill-service.service';

@Component({
  selector: 'app-admin-get-bills-by-area-form',
  templateUrl: './admin-get-bills-by-area-form.component.html',
  styleUrls: ['./admin-get-bills-by-area-form.component.css'],
})
export class AdminGetBillsByAreaFormComponent {
  AdminUserName!: string;
  AdminPassword!: string;
  area!: string;
  Bills: Bill[] = [];
  isHidden: boolean = false;
  errorMsg!: string;
  checkBillsStatus() {
    return this.Bills.length != 0;
  }
  userForm = new FormGroup({
    username: new FormControl('', [Validators.required]),
    password: new FormControl('', [Validators.required]),
    area: new FormControl('', [Validators.required]),
  });

  constructor(private billService: BillServiceService) {}

  onSubmit(data: any) {
    this.AdminUserName = data.username;
    this.AdminPassword = data.password;
    this.area = data.area;
    this.billService
      .getBillsByAreaAdmin(this.AdminUserName, this.AdminPassword, this.area)
      .pipe(
        catchError((error) => {
          this.errorMsg = `Error: ${error.error.message}`;
          return of([]);
        })
      )
      .subscribe((bills) => (this.Bills = bills));
    this.isHidden = true;
  }

  get getUserName() {
    return this.userForm.get('username');
  }
  get getPassword() {
    return this.userForm.get('password');
  }
  get getArea() {
    return this.userForm.get('area');
  }
  onBack() {
    this.isHidden = !this.isHidden;
    this.Bills = [];
  }
}
<div class="container1">
  <h1>Admin</h1>
  <h3>Get All Bill By Area Reports</h3>
  <form
    [formGroup]="userForm"
    (ngSubmit)="onSubmit(userForm.value)"
    method="get"
    [class.hidden]="isHidden"
  >
    <div class="container2">
      <hr />
      <label for="username">Username </label>
      <input
        type="text"
        id="username"
        name="username"
        formControlName="username"
        placeholder="Enter admin username"
      />
      <span
        style="color: red"
        *ngIf="getUserName && getUserName.invalid && getUserName.touched"
        >Username Required!!</span
      >
      <label for="password">Password </label>
      <input
        type="password"
        id="password"
        name="password"
        formControlName="password"
        placeholder="Enter admin password"
      />
      <span
        style="color: red"
        *ngIf="getPassword && getPassword.invalid && getPassword.touched"
        >Password Required!!
      </span>
      <label for="area">Area </label>
      <input
        type="text"
        id="area"
        name="area"
        formControlName="area"
        placeholder="Enter Area"
      />
      <span
        style="color: red"
        *ngIf="getArea && getArea.invalid && getArea.touched"
        >Area Required!!</span
      >
      <div>
        <button [disabled]="userForm.invalid" type="submit" class="submit">
          Submit
        </button>
      </div>
      <hr />
    </div>
  </form>
</div>

<div class="result" [class.hidden]="!isHidden">
  <div *ngIf="checkBillsStatus(); else noResultBlock">
    <div class="container">
      <h2>{{ area }} Bill</h2>
      <table class="table table-striped">
        <thead class="thead-dark">
          <tr class="table-dark">
            <th scope="col">Bill Id</th>
            <th scope="col">Consumer Id</th>
            <th scope="col">Month</th>
            <th scope="col">Year</th>
            <th scope="col">Units Consumed</th>
            <th scope="col">Total Amount</th>
          </tr>
        </thead>
        <tbody>
          <tr *ngFor="let bill of Bills" class="table-info">
            <td>{{ bill.billId }}</td>
            <td>{{ bill.consumerId.consumerId }}</td>
            <td>{{ bill.month }}</td>
            <td>{{ bill.year }}</td>
            <td>{{ bill.unitsConsumed }}</td>
            <td>{{ bill.totalAmount }}</td>
          </tr>
        </tbody>
      </table>
    </div>
  </div>
  <ng-template #noResultBlock><h2>No Bills Found!</h2></ng-template>
  <button (click)="onBack()" class="backbutton">Back</button>
</div>
