import { Component } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { of } from 'rxjs';
import { catchError } from 'rxjs/operators';
import { Bill } from '../bill';
import { BillServiceService } from '../bill-service.service';

@Component({
  selector: 'app-admin-get-all-bills-form',
  templateUrl: './admin-get-all-bills-form.component.html',
  styleUrls: ['./admin-get-all-bills-form.component.css'],
})
export class AdminGetAllBillsFormComponent {
  userName!: string;
  password!: string;
  Bills: Bill[] = [];
  isHidden: boolean = false;
  errorMsg!: string;
  checkBillsStatus() {
    return this.Bills.length != 0;
  }
  userForm = new FormGroup({
    userName: new FormControl('', [Validators.required]),
    password: new FormControl('', [Validators.required]),
  });

  constructor(private billService: BillServiceService) {}

  onSubmit(data: any) {
    this.userName = data.userName;
    this.password = data.password;
    this.billService
      .getAllBillsAdmin(this.userName, this.password)
      .pipe(
        catchError((error) => {
          this.errorMsg = `Error: ${error.error.message}`;
          return of([]);
        })
      )
      .subscribe((bills) => (this.Bills = bills));
    this.isHidden = true;
  }

  get getUsername() {
    return this.userForm.get('userName');
  }

  get getPassword() {
    return this.userForm.get('password');
  }

  onBack() {
    this.isHidden = !this.isHidden;
    this.Bills = [];
  }
}
<div class="container1">
  <h1>Admin</h1>
  <h3>All Bill Reports</h3>
  <form
    [formGroup]="userForm"
    (ngSubmit)="onSubmit(userForm.value)"
    method="get"
    [class.hidden]="isHidden"
  >
    <div class="container2">
      <hr />
      <label for="userName">Username </label>
      <input
        type="text"
        id="username"
        name="userName"
        formControlName="userName"
        placeholder="Enter admin username"
      />
      <span
        style="color: red"
        *ngIf="getUsername && getUsername.invalid && getUsername.touched"
      >
        username Required!!</span
      >
      <br />

      <label for="password"> Password </label>
      <input
        type="password"
        id="password"
        name="password"
        formControlName="password"
        placeholder="Enter admin password"
      />
      <span
        style="color: red"
        *ngIf="getPassword && getPassword.invalid && getPassword.touched"
        >Password Required!!
      </span>
      <br />
      <div>
        <button [disabled]="userForm.invalid" type="submit" class="submit">
          Submit
        </button>
      </div>
    </div>
  </form>
</div>

<div class="result" [class.hidden]="!isHidden">
  <div *ngIf="checkBillsStatus(); else noResultBlock">
    <div class="container">
      <!-- <h2>All Bills</h2> -->
      <table class="table table-striped">
        <thead class="thead-dark">
          <tr class="table-dark">
            <th scope="col">billId</th>
            <th scope="col">Consumer ID</th>
            <th scope="col">Consumer Name</th>
            <th scope="col">Area</th>
            <th scope="col">City</th>
            <th scope="col">Connection Type</th>
            <th scope="col">month</th>
            <th scope="col">year</th>
            <th scope="col">Units Consumed</th>
            <th scope="col">Total Amount</th>
          </tr>
        </thead>
        <tbody>
          <tr *ngFor="let bill of Bills" class="table-info">
            <td>{{ bill.billId }}</td>
            <td>{{ bill.consumerId.consumerId }}</td>
            <td>{{ bill.consumerId.consumerName }}</td>
            <td>{{ bill.consumerId.area }}</td>
            <td>{{ bill.consumerId.city }}</td>
            <td>{{ bill.consumerId.connectionType }}</td>
            <td>{{ bill.month }}</td>
            <td>{{ bill.year }}</td>
            <td>{{ bill.unitsConsumed }}</td>
            <td>{{ bill.totalAmount }}</td>
          </tr>
        </tbody>
      </table>
    </div>
  </div>
  <ng-template #noResultBlock
    ><h2>{{ errorMsg }}</h2></ng-template
  >
  <br />
  <div class="wrap">
    <button (click)="onBack()" class="back-button">Back</button>
  </div>
</div>
