import { Component } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { Bill } from '../bill';
import { BillServiceService } from '../bill-service.service';

@Component({
  selector: 'app-consumer-get-bills-by-month-form',
  templateUrl: './consumer-get-bills-by-month-form.component.html',
  styleUrls: ['./consumer-get-bills-by-month-form.component.css'],
})
export class ConsumerGetBillsByMonthFormComponent {
  month!: string;
  year!: number;
  consumerId!: number;
  isHidden: boolean = false;
  Bills: Bill[] = [];

  checkBillsStatus() {
    return this.Bills.length != 0;
  }
  consumerForm = new FormGroup({
    consumerId: new FormControl('', [Validators.required]),
    month: new FormControl('', [Validators.required]),
    year: new FormControl('', [Validators.required]),
  });
  constructor(private billService: BillServiceService) {}

  onSubmit(data: any) {
    this.month = data.month;
    this.year = data.year;
    this.consumerId = data.consumerId;
    this.billService
      .getBillsByMonthConsumer(this.consumerId, this.month, this.year)
      .subscribe((bills) => (this.Bills = bills));
    this.isHidden = true;
  }
  get getConsumerId() {
    return this.consumerForm.get('consumerId');
  }
  get getMonth() {
    return this.consumerForm.get('month');
  }

  get getYear() {
    return this.consumerForm.get('year');
  }
  onBack() {
    this.isHidden = !this.isHidden;
    this.Bills = [];
  }
}
<div class="container1">
  <h1>Consumer</h1>
  <form
    [formGroup]="consumerForm"
    (ngSubmit)="onSubmit(consumerForm.value)"
    method="get"
    [class.hidden]="isHidden"
  >
    <div class="container2">
      <hr />
      <label for="consumerId">Consumer ID: </label>
      <input
        type="text"
        id="consumerId"
        name="consumerId"
        formControlName="consumerId"
        placeholder="Enter Consumer ID"
      />
      <span
        style="color: red"
        *ngIf="getConsumerId && getConsumerId.invalid && getConsumerId.touched"
        >Consumer ID Required!!</span
      >
      <br />
      <label for="month"> Month </label>

      <select
        class="selectDiv"
        name="month"
        id="month"
        formControlName="month"
        aria-placeholder="Select Month"
      >
        <option value="">Select Month</option>
        <option value="January">January</option>
        <option value="February">February</option>
        <option value="March">March</option>
        <option value="April">April</option>
        <option value="May">May</option>
        <option value="June">June</option>
        <option value="July">July</option>
        <option value="August">August</option>
        <option value="September">September</option>
        <option value="October">October</option>
        <option value="November">November</option>
        <option value="December">December</option>
      </select>

      <span
        style="color: red"
        *ngIf="getMonth && getMonth.invalid && getMonth.touched"
        >Month Required!!
      </span>
      <p>
        <label for="year">Year </label>
        <input
          type="year"
          id="year"
          name="year"
          formControlName="year"
          placeholder="Enter Year"
        />
        <span
          style="color: red"
          *ngIf="getYear && getYear.invalid && getYear.touched"
          >Year Required!!
        </span>
      </p>

      <div>
        <button [disabled]="consumerForm.invalid" type="submit" class="submit">
          Submit
        </button>
      </div>
      <hr />
    </div>
  </form>
</div>

<div class="result" [class.hidden]="!isHidden">
  <div *ngIf="checkBillsStatus(); else noResultBlock">
    <div class="container">
      <h2>{{ month }} {{ year }} Bill</h2>
      <table class="table table-striped">
        <thead class="thead-dark">
          <tr class="table-dark">
            <th scope="col">billId</th>
            <th scope="col">consumerId</th>
            <th scope="col">month</th>
            <th scope="col">year</th>
            <th scope="col">unitsConsumed</th>
            <th scope="col">totalAmount</th>
          </tr>
        </thead>
        <tbody>
          <tr *ngFor="let bill of Bills" class="table-info">
            <td>{{ bill.billId }}</td>
            <td>{{ bill.consumerId.consumerId }}</td>
            <td>{{ bill.month }}</td>
            <td>{{ bill.year }}</td>
            <td>{{ bill.unitsConsumed }}</td>
            <td>{{ bill.totalAmount }}</td>
          </tr>
        </tbody>
      </table>
    </div>
  </div>
  <ng-template #noResultBlock>No Bills Found! </ng-template>
  <button (click)="onBack()">Back</button>
</div>
