import { Component } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { Bill } from '../bill';
import { BillServiceService } from '../bill-service.service';

@Component({
  selector: 'app-consumer-get-bills-by-year-form',
  templateUrl: './consumer-get-bills-by-year-form.component.html',
  styleUrls: ['./consumer-get-bills-by-year-form.component.css'],
})
export class ConsumerGetBillsByYearFormComponent {
  consumerId!: number;
  year!: number;
  Bills: Bill[] = [];
  isHidden: boolean = false;
  checkBillsStatus() {
    return this.Bills.length != 0;
  }
  consumerForm = new FormGroup({
    consumerId: new FormControl('', [Validators.required]),
    year: new FormControl('', [Validators.required]),
  });

  constructor(private billService: BillServiceService) {}

  onSubmit(data: any) {
    this.consumerId = data.consumerId;
    this.year = data.year;
    this.billService
      .getBillsByYearConsumer(this.consumerId, this.year)
      .subscribe((bills) => (this.Bills = bills));
    this.isHidden = true;
  }

  get getConsumerId() {
    return this.consumerForm.get('consumerId');
  }

  get getYear() {
    return this.consumerForm.get('year');
  }
  onBack() {
    this.isHidden = !this.isHidden;
    this.Bills = [];
  }
}
<div class="container1">
  <h1>Consumer</h1>
  <h3>All Bill Reports By Year</h3>
  <form
    [formGroup]="consumerForm"
    (ngSubmit)="onSubmit(consumerForm.value)"
    method="get"
    [class.hidden]="isHidden"
  >
    <div class="container2">
      <hr />
      <label for="consumerId">Consumer ID </label>
      <input
        type="text"
        id="consumerId"
        name="consumerId"
        formControlName="consumerId"
        placeholder="Enter Consumer ID"
      />
      <span
        style="color: red"
        *ngIf="getConsumerId && getConsumerId.invalid && getConsumerId.touched"
        >Consumer ID Required!!</span
      >
      <br />
      <label for="year">Year </label>
      <input
        type="year"
        id="year"
        name="year"
        formControlName="year"
        placeholder="Enter Year"
      />
      <span
        style="color: red"
        *ngIf="getYear && getYear.invalid && getYear.touched"
        >Year Required!!
      </span>
      <div>
        <button [disabled]="consumerForm.invalid" type="submit" class="submit">
          Submit
        </button>
      </div>
    </div>
  </form>
</div>

<div class="result" [class.hidden]="!isHidden">
  <div *ngIf="checkBillsStatus(); else noResultBlock">
    <div class="container">
      <h2>{{ year }} Bill</h2>
      <table class="table table-striped">
        <thead class="thead-dark">
          <tr class="table-dark">
            <th scope="col">billId</th>
            <th scope="col">consumerId</th>
            <th scope="col">month</th>
            <th scope="col">year</th>
            <th scope="col">unitsConsumed</th>
            <th scope="col">totalAmount</th>
          </tr>
        </thead>
        <tbody>
          <tr *ngFor="let bill of Bills" class="table-info">
            <td>{{ bill.billId }}</td>
            <td>{{ bill.consumerId.consumerId }}</td>
            <td>{{ bill.month }}</td>
            <td>{{ bill.year }}</td>
            <td>{{ bill.unitsConsumed }}</td>
            <td>{{ bill.totalAmount }}</td>
          </tr>
        </tbody>
      </table>
    </div>
  </div>
  <ng-template #noResultBlock><h2>No Bills Found!</h2></ng-template>
  <!-- <button (click)="onBack()">Back</button> -->
  <div class="wrap">
    <button (click)="onBack()" class="back-button">Back</button>
  </div>
  <style>
    .wrap {
  height: 100%;
  display: flex;
  align-items: center;
  justify-content: center;
}

.back-button {
  min-width: 300px;
  min-height: 60px;
  font-family: 'Nunito', sans-serif;
  font-size: 22px;
  text-transform: uppercase;
  letter-spacing: 1.3px;
  font-weight: 700;
  color: #313133;
  background: #4FD1C5;
background: linear-gradient(90deg, rgba(129,230,217,1) 0%, rgba(79,209,197,1) 100%);
  border: none;
  border-radius: 1000px;
  box-shadow: 12px 12px 24px rgba(79,209,197,.64);
  transition: all 0.3s ease-in-out 0s;
  cursor: pointer;
  outline: none;
  position: relative;
  padding: 10px;
  }

  .back-button::before {
content: '';
  border-radius: 1000px;
  min-width: calc(300px + 12px);
  min-height: calc(60px + 12px);
  border: 6px solid #00FFCB;
  box-shadow: 0 0 60px rgba(0,255,203,.64);
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  opacity: 0;
  transition: all .3s ease-in-out 0s;
}

.back-button:hover, .button:focus {
  color: #313133;
  transform: translateY(-6px);
}

.back-button:hover::before, button:focus::before {
  opacity: 1;
}

.back-button::after {
  content: '';
  width: 30px; height: 30px;
  border-radius: 100%;
  border: 6px solid #00FFCB;
  position: absolute;
  z-index: -1;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  animation: ring 1.5s infinite;
}

.back-button:hover::after, button:focus::after {
  animation: none;
  display: none;
}

@keyframes ring {
  0% {
    width: 30px;
    height: 30px;
    opacity: 1;
  }
  100% {
    width: 300px;
    height: 300px;
    opacity: 0;
  }
}

  </style>
</div>
