import { Component } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { BillServiceService } from '../bill-service.service';

@Component({
  selector: 'app-admin-add-units-form',
  templateUrl: './admin-add-units-form.component.html',
  styleUrls: ['./admin-add-units-form.component.css'],
})
export class AdminAddUnitsFormComponent {
  AdminUserName!: string;
  AdminPassword!: string;
  consumerId!: number;
  year!: number;
  month!: string;
  unitsConsumed!: number;

  isHidden: boolean = false;
  addUnitsForm = new FormGroup({
    username: new FormControl('', [Validators.required]),
    password: new FormControl('', [Validators.required]),
  });

  constructor(private billService: BillServiceService) {}

  onSubmit(data: any) {
    this.AdminUserName = data.username;
    this.AdminPassword = data.password;

    this.isHidden = true;
  }

  get getUserName() {
    return this.addUnitsForm.get('username');
  }
  get getPassword() {
    return this.addUnitsForm.get('password');
  }
}
<form
  [formGroup]="addUnitsForm"
  (ngSubmit)="onSubmit(addUnitsForm.value)"
  method="get"
  [class.hidden]="isHidden"
>
  <label for="username">Username: </label>
  <input
    type="text"
    id="username"
    name="username"
    formControlName="username"
    placeholder="Enter admin username"
  />
  <span
    style="color: red"
    *ngIf="getUserName && getUserName.invalid && getUserName.touched"
    >Username Required!!</span
  >
  <br />
  <label for="password">Password: </label>
  <input
    type="password"
    id="password"
    name="password"
    formControlName="password"
    placeholder="Enter admin password"
  />
  <span
    style="color: red"
    *ngIf="getPassword && getPassword.invalid && getPassword.touched"
    >Password Required!!
  </span>
  <div>
    <button [disabled]="addUnitsForm.invalid" type="submit">Submit</button>
  </div>
</form>
<div class="result" [class.hidden]="!isHidden">
  <div *ngIf="false; else noResultBlock">
    <h3>Units Added Successfully</h3>
  </div>
  <ng-template #noResultBlock
    >There was some error adding units. Please check the values
    again.</ng-template
  >
  <button (click)="isHidden = !isHidden">Back</button>
</div>
