import { Component } from '@angular/core';

@Component({
  selector: 'app-consumer-registration-form',
  templateUrl: './consumer-registration-form.component.html',
  styleUrls: ['./consumer-registration-form.component.css'],
})
export class ConsumerRegistrationFormComponent {}
import { Component } from '@angular/core';

@Component({
  selector: 'app-admin-get-bills-by-month-form',
  templateUrl: './admin-get-bills-by-month-form.component.html',
  styleUrls: ['./admin-get-bills-by-month-form.component.css'],
})
export class AdminGetBillsByMonthFormComponent {
  userName!: string;
  password!: string;
  month!: any;
  year!: number;
  Bills: Bill[] = [];
  isHidden: boolean = false;
  checkBillsStatus() {
    return this.Bills.length != 0;
  }
  userForm = new FormGroup({
    userName: new FormControl('', [Validators.required]),
    password: new FormControl('', [Validators.required]),
    month: new FormControl('', Validators.required),
    year: new FormControl('', [Validators.required]),
  });

  constructor(private billService: BillServiceService) {}

  onSubmit(data: any) {
    this.userName = data.userName;
    this.password = data.password;
    this.month = data.month;
    this.year = data.year;
    this.billService
      .getBillsByMonthAdmin(this.userName, this.password, this.month, this.year)
      .subscribe((bills) => (this.Bills = bills));
    this.isHidden = true;
  }
  get getUsername() {
    return this.userForm.get('userName');
  }

  get getPassword() {
    return this.userForm.get('password');
  }

  get getYear() {
    return this.userForm.get('year');
  }
  get getMonth() {
    return this.userForm.get('month');
  }
  onBack() {
    this.isHidden = !this.isHidden;
    this.Bills = [];
  }
}
