import { Component } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { Bill } from '../bill';
import { BillServiceService } from '../bill-service.service';

@Component({
  selector: 'app-admin-get-bills-by-month-form',
  templateUrl: './admin-get-bills-by-month-form.component.html',
  styleUrls: ['./admin-get-bills-by-month-form.component.css'],
})
export class AdminGetBillsByMonthFormComponent {
  userName!: string;
  password!: string;
  month!: any;
  year!: number;
  Bills: Bill[] = [];
  isHidden: boolean = false;
  checkBillsStatus() {
    return this.Bills.length != 0;
  }
  userForm = new FormGroup({
    userName: new FormControl('', [Validators.required]),
    password: new FormControl('', [Validators.required]),
    month: new FormControl('', Validators.required),
    year: new FormControl('', [Validators.required]),
  });

  constructor(private billService: BillServiceService) {}

  onSubmit(data: any) {
    this.userName = data.userName;
    this.password = data.password;
    this.month = data.month;
    this.year = data.year;
    this.billService
      .getBillsByMonthAdmin(this.userName, this.password, this.month, this.year)
      .subscribe((bills) => (this.Bills = bills));
    this.isHidden = true;
  }
  get getUsername() {
    return this.userForm.get('userName');
  }

  get getPassword() {
    return this.userForm.get('password');
  }

  get getYear() {
    return this.userForm.get('year');
  }
  get getMonth() {
    return this.userForm.get('month');
  }
  onBack() {
    this.isHidden = !this.isHidden;
    this.Bills = [];
  }
}
<div class="container1">
  <h1>Admin</h1>
  <h3>All Bill Reports By Month</h3>
  <form
    [formGroup]="userForm"
    (ngSubmit)="onSubmit(userForm.value)"
    method="get"
    [class.hidden]="isHidden"
  >
    <div class="container2">
      <hr />
      <label for="userName">Username </label>
      <input
        type="text"
        id="username"
        name="userName"
        formControlName="userName"
        placeholder="Enter admin username"
      />
      <span
        style="color: red"
        *ngIf="getUsername && getUsername.invalid && getUsername.touched"
      >
        username Required!!</span
      >
      <br />

      <label for="password"> Password </label>
      <input
        type="password"
        id="password"
        name="password"
        formControlName="password"
        placeholder="Enter admin password"
      />
      <span
        style="color: red"
        *ngIf="getPassword && getPassword.invalid && getPassword.touched"
        >Password Required!!
      </span>
      <br />

      <label for="month"> Month </label>

      <select
        class="selectDiv"
        name="month"
        id="month"
        formControlName="month"
        aria-placeholder="Select Month"
      >
        <option value="">Select Month</option>
        <option value="January">January</option>
        <option value="February">February</option>
        <option value="March">March</option>
        <option value="April">April</option>
        <option value="May">May</option>
        <option value="June">June</option>
        <option value="July">July</option>
        <option value="August">August</option>
        <option value="September">September</option>
        <option value="October">October</option>
        <option value="November">November</option>
        <option value="December">December</option>
      </select>

      <span
        style="color: red"
        *ngIf="getMonth && getMonth.invalid && getMonth.touched"
        >Month Required!!
      </span>
      <p>
        <label for="year">Year </label>
        <input
          type="year"
          id="year"
          name="year"
          formControlName="year"
          placeholder="Enter Year"
        />
        <span
          style="color: red"
          *ngIf="getYear && getYear.invalid && getYear.touched"
          >Year Required!!
        </span>
      </p>

      <div>
        <button [disabled]="userForm.invalid" type="submit" class="submit">
          Submit
        </button>
      </div>
    </div>
  </form>
</div>

<div class="result" [class.hidden]="!isHidden">
  <div *ngIf="checkBillsStatus(); else noResultBlock">
    <div class="container">
      <h2>{{ month }} {{ year }} Bill</h2>
      <table class="table table-striped">
        <thead class="thead-dark">
          <tr class="table-dark">
            <th scope="col">billId</th>
            <th scope="col">consumerId</th>
            <th scope="col">month</th>
            <th scope="col">year</th>
            <th scope="col">unitsConsumed</th>
            <th scope="col">totalAmount</th>
          </tr>
        </thead>
        <tbody>
          <tr *ngFor="let bill of Bills" class="table-info">
            <td>{{ bill.billId }}</td>
            <td>{{ bill.consumerId.consumerId }}</td>
            <td>{{ bill.month }}</td>
            <td>{{ bill.year }}</td>
            <td>{{ bill.unitsConsumed }}</td>
            <td>{{ bill.totalAmount }}</td>
          </tr>
        </tbody>
      </table>
    </div>
  </div>
  <ng-template #noResultBlock><h2>No Bills Found!</h2></ng-template>
  <button (click)="onBack()">Back</button>
</div>
