import { Component } from '@angular/core';

@Component({
  selector: 'app-consumer-registration-form',
  templateUrl: './consumer-registration-form.component.html',
  styleUrls: ['./consumer-registration-form.component.css'],
})
export class ConsumerRegistrationFormComponent {}
import { Component } from '@angular/core';

@Component({
  selector: 'app-admin-get-bills-by-month-form',
  templateUrl: './admin-get-bills-by-month-form.component.html',
  styleUrls: ['./admin-get-bills-by-month-form.component.css'],
})
export class AdminGetBillsByMonthFormComponent {
  userName!: string;
  password!: string;
  month!: any;
  year!: number;
  Bills: Bill[] = [];
  isHidden: boolean = false;
  checkBillsStatus() {
    return this.Bills.length != 0;
  }
  userForm = new FormGroup({
    userName: new FormControl('', [Validators.required]),
    password: new FormControl('', [Validators.required]),
    month: new FormControl('', Validators.required),
    year: new FormControl('', [Validators.required]),
  });

  constructor(private billService: BillServiceService) {}

  onSubmit(data: any) {
    this.userName = data.userName;
    this.password = data.password;
    this.month = data.month;
    this.year = data.year;
    this.billService
      .getBillsByMonthAdmin(this.userName, this.password, this.month, this.year)
      .subscribe((bills) => (this.Bills = bills));
    this.isHidden = true;
  }
  get getUsername() {
    return this.userForm.get('userName');
  }

  get getPassword() {
    return this.userForm.get('password');
  }

  get getYear() {
    return this.userForm.get('year');
  }
  get getMonth() {
    return this.userForm.get('month');
  }
  onBack() {
    this.isHidden = !this.isHidden;
    this.Bills = [];
  }
}
<div class="container1">
  <h1>Consumer</h1>
  <h3>New Registration</h3>
  <form
    [formGroup]="consumerForm"
    (ngSubmit)="onSubmit(consumerForm.value)"
    method="get"
    [class.hidden]="isHidden"
  >
    <div class="container2">
      <hr />
      <label for="name">Full Name </label>
      <input
        type="text"
        id="name"
        name="name"
        formControlName="name"
        placeholder="Enter your name"
      />
      <span
        style="color: red"
        *ngIf="getName && getName.invalid && getName.touched"
      >
        Full name Required!!</span
      >
      <br />
      <label for="area">Area </label>
      <input
        type="text"
        id="area"
        name="area"
        formControlName="area"
        placeholder="Enter your area"
      />
      <span
        style="color: red"
        *ngIf="getArea && getArea.invalid && getArea.touched"
      >
        Area Required!!</span
      >
      <br />

      <label for="city">City </label>
      <input
        type="text"
        id="city"
        name="city"
        formControlName="city"
        placeholder="Enter your city"
      />
      <span
        style="color: red"
        *ngIf="getCity && getCity.invalid && getCity.touched"
      >
        City Required!!</span
      >
      <br />

      <label for="ctype">Connection Type </label>
      <input
        type="text"
        id="ctype"
        name="ctype"
        formControlName="ctype"
        placeholder="Enter your connection type"
      />
      <span
        style="color: red"
        *ngIf="getType && getType.invalid && getType.touched"
      >
        username Required!!</span
      >
      <br />

      <div>
        <button [disabled]="consumerForm.invalid" type="submit" class="submit">
          Submit
        </button>
      </div>
    </div>
  </form>
</div>

<div class="result" [class.hidden]="!isHidden">
  <div *ngIf="checkBillsStatus(); else noResultBlock"></div>
  <ng-template #noResultBlock>No Bills Found! </ng-template>
  <button (click)="onBack()">Back</button>
</div>
